import styled, { css } from 'styled-components';


export const Container = styled.div`
grid-area: AS;

background-color: ${props => props.theme.colors.secondary};
padding-left: 20px;

border-right: 1px solid ${props => props.theme.colors.gray};

position: relative;

@media(max-width: 600px){
    padding-left: 20px;
    position: fixed;
    z-index: 2;

    width: 170px;
}
`;
