import React, { useMemo, useState, useEffect } from 'react';

import  ContentHeader  from '../../components/ContentHeader';
import { useParams } from 'react-router-dom';
import  SelectInput  from '../../components/SelectInput';
import JobMonitoring from '../../components/JobMonitoring';

import radar_run from '../../repositories/radar_run';
import myna_run from '../../repositories/myna_run';

import formatDate from '../../utils/formatDate';

import { Container, Content, Filters } from './styles';

interface IRouteParams {
    job: string;
}

interface IData {
    id: string;
    status: string;
    date: string;
    tagColor: string;
}

const List: React.FC = () => {

    const [data, setData] = useState<IData[]>([]);

    const [monthSelected, setMonthSelected] =  useState<string>(String(new Date().getMonth() + 1)) 

    const [yearSelected, setYearSelected] =  useState<string>(String(new Date().getFullYear())) 


    const { job } = useParams<keyof IRouteParams>() as IRouteParams;

    const title = useMemo(() => {
        return  job === 'entry-myna' ? 'Myna monitoring': 'Radar monitoring'
    }, [job]);

    const lineColor = useMemo(() => {
        return  job === 'entry-myna' ? '#F7931B': '#E44C4E'
    }, [job]);


   const listData = useMemo(() => {
    return  job === 'entry-myna' ? myna_run: radar_run;
    }, [job]);

    const months = [
        {value: 1, label: 'Janeiro'},
        {value: 2, label: 'Fevereiro'},
        {value: 3, label: 'Março'},
        {value: 4, label: 'Abril'},
        {value: 5, label: 'Maio'},
        {value: 6, label: 'Junho'},
        {value: 7, label: 'Julho'},
        {value: 8, label: 'Agosto'},
        {value: 9, label: 'Setembro'},
        {value: 10, label: 'Outubro'},
        {value: 11, label: 'Novembro'},
        {value: 12, label: 'Dezembro'}
    ];

    const years = [
        {value: 2016, label: 2016},
        {value: 2017, label: 2017},
        {value: 2018, label: 2018},
        {value: 2019, label: 2019},
        {value: 2020, label: 2020},
        {value: 2021, label: 2021},
        {value: 2022, label: 2022},
        {value: 2023, label: 2023},
        {value: 2024, label: 2024}
    ];

    useEffect(() => {
        const  response = listData.map(item => {
            return{
                id: String(Math.random() * data.length),
                status: item.status,
                date: formatDate(item.date),
                tagColor: item.status === 'Successfull' ? "#41f067" : "#f04141"
            }
           
        })
        setData(response);
    },[]);

    return (
        <Container>
        <ContentHeader title ={title} lineColor={lineColor}>
            <SelectInput options= {months} onChange= { (e) => setMonthSelected(e.target.value)} defaultValue={monthSelected}/>
            <SelectInput options= {years} onChange= { (e) => setYearSelected(e.target.value)} defaultValue={yearSelected}/>
        </ContentHeader>

        <Filters>
                <button 
                    type="button"
                    className=
                    {`tag-filter
                    tag-filter-success`}
                >
                    Success
                </button>

                <button 
                    type="button"
                    className= 
                    {`tag-filter
                    tag-filter-failed`
                    }
                >
                    Failed
                </button>
        </Filters>
        <Content>
            {
                data.map(item =>(
                    <JobMonitoring 
                    key = {item.id}
                    tagColor = {item.tagColor}
                    date = {item.date}
                    status = {item.status}
                    />

                ))
            
           
            }
        </Content>

    </Container>
    );
    }
export default List;
