import React from 'react';
import {
    ResponsiveContainer,
    BarChart,
    Bar,
    Cell,
    Tooltip,
} from 'recharts';

//import formatCurrency from '../../utils/formatCurrency';

import { 
    Container,
    SideLeft,
    SideRight,
    LegendContainer,
    Legend,
}  from './styles';

interface IBarChartProps {
    title: string;
    data: {
        name: string;
        percent: number;
        color: string;
    }[],
}

const BarChartBox: React.FC<IBarChartProps> = ({
    title,
    data
}) => (
        <Container>
            <SideLeft>
                <h2>{title}</h2>
            </SideLeft>
            
            <SideRight>
            <ResponsiveContainer>
                    <BarChart data={data}>                    
                        <Bar dataKey="amount" name="Valor">
                            {
                                data.map((indicator) => (
                                    <Cell 
                                        key={indicator.name}
                                        fill={indicator.color}
                                        cursor="pointer"    
                                    />
                                ))
                            }
                        </Bar>   
                        <Tooltip 
                            cursor={{fill: 'none'}}
                            formatter={(value) => formatCurrency(Number(value))} 
                        />
                    </BarChart>
                </ResponsiveContainer>
               
            </SideRight>       
        </Container>
    );

export default BarChartBox;
