import React, { useMemo, useState } from 'react';

import  ContentHeader  from '../../components/ContentHeader';
import  SelectInput  from '../../components/SelectInput';

import myna_run from '../../repositories/myna_run';

import radar_run from '../../repositories/radar_run';

import listOfMonths from '../../utils/months';

import { Container } from './styles';

const Dashboard: React.FC = () => {

    const [monthSelected, setMonthSelected] = useState<number>(new Date().getMonth() + 1);
    const [yearSelected, setYearSelected] = useState<number>(new Date().getFullYear());
   
    const options = [
        {value: 'rafa', label: 'rafa'},
        {value: 'charlie', label: 'charlie',}
    ];

    const years = useMemo(() => {
        let uniqueYears: number[] = [];

        [...myna_run, ...radar_run].forEach(item => {
            const date = new Date(item.date);
            const year = date.getFullYear();

            if(!uniqueYears.includes(year)){
                uniqueYears.push(year)
           }

           uniqueYears.sort();
        });

        return uniqueYears.map(year => {
            return {
                value: year,
                label: year,
            }
        });
    },[]);


    const months = useMemo(() => {
        return listOfMonths.map((month, index) => {
            return {
                value: index + 1,
                label: month,
            }
        });
    },[]);


    const handleMonthSelected = (month: string) => {
        try {
            const parseMonth = Number(month);
            setMonthSelected(parseMonth);
        }
        catch{
            throw new Error('invalid month value. Is accept 0 - 24.')
        }
    }

    const handleYearSelected = (year: string) => {
        try {
            const parseYear = Number(year);
            setYearSelected(parseYear);
        }
        catch{
            throw new Error('invalid year value. Is accept integer numbers.')
        }
    }

    return (
        <Container>
            <ContentHeader title ="Dahsboard" lineColor='#F7931B'>
                <SelectInput 
                options= {months} 
                onChange= { (e) => setMonthSelected(Number(e.target.value))} 
                defaultValue={monthSelected}
                />
                <SelectInput 
                options= {years} 
                onChange= { (e) => setYearSelected(Number(e.target.value))} 
                defaultValue={yearSelected}
                />
            </ContentHeader>

        </Container>
    );
    }
export default Dashboard;
