import styled from "styled-components";

interface IContainerProps {
    color: string;

}

export const Container = styled.div<IContainerProps>`
    width: 32%;
    height: 100px;
    margin: 10px 0;
    background-color: ${props => props.color};
    color: ${props => props.theme.colors.white};
    border-radius: 7px;
    padding: 10px 10px;
    position: relative;

    > span {
        font-size: 16px;
        font-weight: 500;
    }

    > small {
        font-size: 12px;
        position: absolute;
        bottom: 5px ;
    }
`
